using System; using System.Collections.Generic; using System.Diagnostics; using System.Diagnostics.Contracts; using System.Linq; using System.Threading.Tasks; using Microsoft.AspNetCore.Mvc; using Webshop.Models;  namespace Webshop.Controllers {     public class HomeController : Controller     {         public IActionResult Index()         {             return View();         }           public string GetCartCookie()         {             var cartId = Request.Cookies["CartID"];             if (cartId == null)             {                 Guid guid = Guid.NewGuid();                 Response.Cookies.Append("CartID", guid.ToString());                 return guid.ToString();             }             return cartId;         }          public IActionResult Error()         {             return View(new ErrorViewModel { RequestId = Activity.Current?.Id ?? HttpContext.TraceIdentifier });         }      } }